{
    unsigned char out[2];

    code_loc = (code_loc + 1) & 0xFFFF;
    if (pass == 1)
        return;
    /* FIXME: just htons... */
    out[0] = (val >> 8);
    out[1] = (val & 0xFF);
    fwrite (out, 2, 1, objout);

    /* print to the out_file the hex instruction (the input to the function called val)*/
    fprintf (out_file, "%x\n", val);
}

